import React, { useState, useEffect } from 'react';
import Swal from 'sweetalert2';
import { UserAuth } from '../context/AuthContext';
import { useNavigate } from 'react-router-dom';
import Register from './Register';
import { Container, Box, Typography, Button, Modal, IconButton } from '@mui/material';
import CloseIcon from '@mui/icons-material/Close';

const Account = () => {
  const { logOut, user } = UserAuth();
  const navigate = useNavigate();
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [userRole, setUserRole] = useState('');

  const handleSignOut = async () => {
    try {
      await logOut();
      navigate('/');
    } catch (error) {
      console.error('Erro ao deslogar:', error);
    }
  };

  const handleRegisterClick = () => setIsModalOpen(true);
  const closeModal = () => setIsModalOpen(false);

  useEffect(() => {
    if (user) {
      const emailDomain = user.email.split('@')[1];
      if (emailDomain === 'aluno.ifsc.edu.br') {
        setUserRole('aluno');
      } else if (emailDomain === 'ifsc.edu.br') {
        setUserRole('professor');
      } else {
        setUserRole('outro');
      }
    }
  }, [user]);

  useEffect(() => {
    if (!user) {
      navigate('/');
    } else if (userRole === 'outro') {
      Swal.fire({
        title: 'Atenção',
        text: 'Por favor, use um e-mail institucional para acessar esta área.',
        icon: 'error',
        confirmButtonText: 'OK',
      }).then(() => navigate('/'));
    }
  }, [user, userRole, navigate]);

  if (!user) return null;

  return (
    <Container maxWidth="md" sx={{ mt: 4 }}>
      <Box display="flex" justifyContent="space-between" alignItems="center" mb={4}>
        <Typography variant="h4" fontWeight="bold">
          {userRole === 'aluno' ? 'Painel do Aluno' : 'Painel do Professor'}
        </Typography>
        <Button variant="contained" color="error" onClick={handleSignOut}>
          Sair
        </Button>
      </Box>

      <Button variant="contained" color="primary" onClick={handleRegisterClick}>
        Registrar Horas
      </Button>

      <Modal open={isModalOpen} onClose={closeModal}>
        <Box
          sx={{
            position: 'absolute',
            top: '50%',
            left: '50%',
            transform: 'translate(-50%, -50%)',
            width: 600,
            bgcolor: 'background.paper',
            boxShadow: 24,
            p: 4,
            borderRadius: 2,
          }}
        >
          <IconButton
            sx={{ position: 'absolute', top: 8, right: 8 }}
            onClick={closeModal}
          >
            <CloseIcon />
          </IconButton>
          <Register />
        </Box>
      </Modal>
    </Container>
  );
};

export default Account;
