import React, { useState } from "react";
import { GoogleOAuthProvider, GoogleLogin } from '@react-oauth/google';

function App() {
    const [name, setName] = useState('');
    const [email, setEmail] = useState('');
    const [isLoggedIn, setIsLoggedIn] = useState(false);
    const [errorMessage, setErrorMessage] = useState('');

    const handleLoginSuccess = async (credentialResponse) => {
        const { credential } = credentialResponse; // Acessa o token de credencial
        console.log("Token de credencial:", credential);

        try {
            const response = await fetch("https://www.googleapis.com/oauth2/v3/userinfo", {
                method: "GET",
                headers: {
                    Authorization: `Bearer ${credential}`,
                },
            });

            if (!response.ok) {
                const errorDetails = await response.json(); // Obter detalhes do erro
                console.error("Erro na resposta da API:", errorDetails);
                throw new Error(`HTTP error! status: ${response.status}`);
            }

            const userProfile = await response.json();
            console.log("Perfil do usuário:", userProfile);
            setName(userProfile.name);
            setEmail(userProfile.email);
            setIsLoggedIn(true);
            setErrorMessage(''); // Limpa mensagem de erro
        } catch (error) {
            console.error("Erro ao obter informações do usuário:", error);
            setErrorMessage("Erro ao obter informações do usuário. Por favor, tente novamente.");
        }
    };

    return (
        <GoogleOAuthProvider clientId="330895842307-cprcpq4q042vvl6mcjqlvgpem1ip8nf4.apps.googleusercontent.com">
            <div className="container">
                <GoogleLogin
                    onSuccess={handleLoginSuccess}
                    onError={() => {
                        console.log('Login Failed');
                    }}
                />
                {isLoggedIn ? (
                    <div style={{ textAlign: "center" }}>
                        <h1>User Information</h1>
                        <p>Name: {name}</p>
                        <p>Email: {email}</p>
                    </div>
                ) : (
                    <p style={{ textAlign: "center" }}>Por favor, faça login para ver suas informações.</p>
                )}
                {errorMessage && (
                    <p style={{ color: 'red', textAlign: "center" }}>{errorMessage}</p>
                )}
            </div>
        </GoogleOAuthProvider>
    );
}

export default App;
