import React, { useState, useEffect } from 'react';
import Swal from 'sweetalert2';
import { UserAuth } from '../context/AuthContext';
import { useNavigate } from 'react-router-dom';
import Register from './Register'; // Importando o componente Register

const Account = () => {
  const { logOut, user } = UserAuth();
  const navigate = useNavigate();
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [userRole, setUserRole] = useState('');

  const handleSignOut = async () => {
    try {
      await logOut();
    } catch (error) {
      console.error(error);
    }
  };

  const handleRegisterClick = () => setIsModalOpen(true);
  const closeModal = () => setIsModalOpen(false);

  // Lógica para determinar o tipo de usuário com base no e-mail
  useEffect(() => {
    if (user) {
      const emailDomain = user.email.split('@')[1];
      if (emailDomain === 'aluno.ifsc.edu.br') {
        setUserRole('aluno');
      } else if (emailDomain === 'ifsc.edu.br') {
        setUserRole('professor');
      } else {
        setUserRole('outro');
      }
    }
  }, [user]);

  // Redireciona usuário não válido
  useEffect(() => {
    if (user && userRole === 'outro') {
      Swal.fire({
        title: 'Atenção',
        text: 'Por favor, use um e-mail institucional para acessar esta área.',
        icon: 'error',
        confirmButtonText: 'OK',
      }).then(() => {
        navigate('/outra-pagina');
      });
    }
  }, [user, userRole, navigate]);

  // Mensagem de boas-vindas
  useEffect(() => {
    if (user && userRole === 'aluno') {
      Swal.fire({
        title: 'Bem-vindo!',
        text: 'Aqui você pode cadastrar e acompanhar suas horas complementares.',
        icon: 'success',
        confirmButtonText: 'OK',
      });
    }
  }, [user, userRole]);

  // Oculta a interface para usuários inválidos
  if (userRole === 'outro') {
    return null;
  }

  return (
    <div className="p-4 bg-gray-50 min-h-screen">
      {/* Botão de registro de horas */}
      {userRole && (
        <button
          onClick={handleRegisterClick}
          className="bg-gray-300 px-4 py-2 rounded hover:bg-gray-400 transition"
        >
          Registrar Horas
        </button>
      )}

      {/* Modal de registro */}
      {isModalOpen && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center">
          <div className="bg-white p-6 rounded shadow-md w-full max-w-lg">
            <button
              onClick={closeModal}
              className="absolute top-4 right-4 text-gray-400 hover:text-gray-600"
            >
              ✕
            </button>
            <Register />
          </div>
        </div>
      )}
    </div>
  );
};

export default Account;
