import React, { useState } from 'react';
import { signInWithPopup } from 'firebase/auth';
import { auth, provider } from './firebaseConfig';
import { Button } from '@mui/material';
import { useNavigate } from 'react-router-dom';

const Login = () => {
  const navigate = useNavigate();
  const [error, setError] = useState(null);

  const handleLogin = async () => {
    try {
      // Realiza a autenticação com o Google
      const result = await signInWithPopup(auth, provider);
      const user = result.user;

      // Verifica o domínio do e-mail para redirecionar
      const email = user.email;
      if (email.includes('@aluno')) {
        navigate('/aluno');  // Redireciona para a página do aluno
      } else if (email.includes('@ifsc')) {
        navigate('/professor');  // Redireciona para a página do professor
      } else {
        setError('E-mail não autorizado');
      }
    } catch (error) {
      setError('Erro ao fazer login: ' + error.message);
    }
  };

  return (
    <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '100vh' }}>
      <div>
        <Button onClick={handleLogin} variant="contained" color="primary">
          Fazer login com Google
        </Button>
        {error && <p style={{ color: 'red' }}>{error}</p>}
      </div>
    </div>
  );
};

export default Login;
