import React, { useState, useEffect } from 'react';
import Swal from 'sweetalert2';
import { UserAuth } from '../context/AuthContext';
import { useNavigate } from 'react-router-dom';
import Register from './Register'; // Importando o componente Register

const Account = () => {
  const { logOut, user } = UserAuth(); // Recupera o estado do usuário do contexto
  const navigate = useNavigate(); // Hook para redirecionar para outras páginas
  const [isModalOpen, setIsModalOpen] = useState(false); // Estado para controle do modal
  const [userRole, setUserRole] = useState(''); // Variável para armazenar o tipo de usuário

  const handleSignOut = async () => {
    try {
      await logOut();
    } catch (error) {
      console.log(error);
    }
  };

  const handleRegisterClick = () => {
    setIsModalOpen(true); // Abre o modal ao clicar no botão
  };

  const closeModal = () => {
    setIsModalOpen(false); // Fecha o modal
  };

  useEffect(() => {
    if (user) {
      // Dividindo o email para verificar apenas a parte após o '@'
      const emailDomain = user.email.split('@')[1]; // Parte do email após o '@'
      
      if (emailDomain === 'aluno.ifsc.edu.br') {
        setUserRole('aluno'); // Se o domínio for 'aluno.ifsc.edu.br'
      } else if (emailDomain === 'ifsc.edu.br') {
        setUserRole('professor'); // Se o domínio for 'ifsc.edu.br'
      } else {
        setUserRole('outro'); // Tipo de usuário diferente
      }
    }
  }, [user]);
  

  // Redireciona o usuário para a página adequada caso não seja aluno
  useEffect(() => {
    if ((user && userRole !== 'aluno') && (user && userRole !== "professor")){
      Swal.fire({
        title: 'Olá',
        text: 'Para cadastrar suas horas complementares é preciso logar com o e-mail institucional',
        icon: 'error',
        confirmButtonText: 'OK'
      });
    }
  }, [user, userRole, navigate]); // Verifica quando o usuário ou o role mudar

  useEffect(() => {
    if (user) {
      Swal.fire({
        title: 'Bem-vindo!',
        text: 'Aqui você pode cadastrar e acompanhar suas horas complementares.',
        icon: 'success',
        confirmButtonText: 'OK'
      });
    }
  }, [user]); // Exibe a mensagem apenas quando o usuário estiver logado

  return (
    <div className="p-6 bg-gray-100 min-h-screen">
      {/* Botão de abrir o modal */}
      <button
        onClick={handleRegisterClick}
        className="bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600"
      >
        Registrar horas
      </button>

      {/* Modal do formulário de registrar horas */}
      {isModalOpen && (
        <div className="fixed inset-0 bg-gray-800 bg-opacity-75 flex items-center justify-center z-50">
          <div className="bg-white p-6 rounded-md w-1/2 shadow-lg">
            <button
              onClick={closeModal}
              className="absolute top-2 right-2 text-gray-500 hover:text-gray-700"
            >
              X
            </button>
            {/* Renderizando o formulário da página Register */}
            <Register />
          </div>
        </div>
      )}
    </div>
  );
};

export default Account;
