import React, { useState } from 'react';
import { Bar } from 'react-chartjs-2';
import {
  Chart as ChartJS,
  BarElement,
  CategoryScale,
  LinearScale,
  Tooltip,
  Legend,
} from 'chart.js';

ChartJS.register(BarElement, CategoryScale, LinearScale, Tooltip, Legend);

const Dashboard = () => {
  const [menu, setMenu] = useState('overview'); // Gerenciar o menu ativo
  const [isModalOpen, setIsModalOpen] = useState(false); // Controlar o estado do modal

  const handleNavigation = (section) => {
    setMenu(section); // Atualiza o menu selecionado
  };

  const openModal = () => {
    setIsModalOpen(true); // Abre o modal
  };

  const closeModal = () => {
    setIsModalOpen(false); // Fecha o modal
  };

  // Dados para o gráfico
  const data = {
    labels: ['Categoria A', 'Categoria B', 'Categoria C', 'Categoria D'], // Categorias
    datasets: [
      {
        label: 'Horas Registradas',
        data: [12, 19, 8, 15], // Dados fictícios
        backgroundColor: [
          'rgba(255, 99, 132, 0.2)',
          'rgba(54, 162, 235, 0.2)',
          'rgba(255, 206, 86, 0.2)',
          'rgba(75, 192, 192, 0.2)',
        ],
        borderColor: [
          'rgba(255, 99, 132, 1)',
          'rgba(54, 162, 235, 1)',
          'rgba(255, 206, 86, 1)',
          'rgba(75, 192, 192, 1)',
        ],
        borderWidth: 1,
      },
    ],
  };

  const options = {
    responsive: true,
    plugins: {
      legend: {
        position: 'top',
      },
      tooltip: {
        enabled: true,
      },
    },
    scales: {
      y: {
        beginAtZero: true,
      },
    },
  };

  return (
    <div className="h-screen flex flex-col">
      {/* Barra de navegação no topo */}
      <nav className="bg-gray-800 text-white">
        <div className="flex justify-between items-center px-6 py-4">
          <h1 className="text-2xl font-bold">Dashboard</h1>
          <div className="space-x-4">
            <button
              className={`px-4 py-2 rounded ${
                menu === 'overview' ? 'bg-gray-700' : 'hover:bg-gray-700'
              }`}
              onClick={() => handleNavigation('overview')}
            >
              Visão Geral
            </button>
            <button
              className={`px-4 py-2 rounded ${
                menu === 'register' ? 'bg-gray-700' : 'hover:bg-gray-700'
              }`}
              onClick={openModal}
            >
              Registrar Horas
            </button>
            <button
              className={`px-4 py-2 rounded ${
                menu === 'profile' ? 'bg-gray-700' : 'hover:bg-gray-700'
              }`}
              onClick={() => handleNavigation('profile')}
            >
              Meu Perfil
            </button>
          </div>
        </div>
      </nav>

      {/* Área principal */}
      <main className="flex-1 bg-gray-100 p-6">
        {menu === 'overview' && (
          <div>
            <h2 className="text-2xl font-bold mb-4">Visão Geral</h2>
            {/* Gráfico de barras */}
            <div className="w-full md:w-1/2 mx-auto">
              <Bar data={data} options={options} />
            </div>
          </div>
        )}
        {menu === 'profile' && <h2>Aqui você pode ver e editar seu perfil.</h2>}
      </main>

      {/* Modal do formulário */}
      {isModalOpen && (
        <div className="fixed inset-0 bg-gray-800 bg-opacity-75 flex items-center justify-center z-50">
          <div className="bg-white p-6 rounded-md w-1/3 shadow-lg relative">
            <button
              className="absolute top-2 right-2 text-gray-500 hover:text-gray-700"
              onClick={closeModal}
            >
              X
            </button>
            <h2 className="text-xl font-bold mb-4">Registrar Horas</h2>
            <form>
              <div className="mb-4">
                <label className="block text-gray-700 font-bold mb-2">Descrição</label>
                <input
                  type="text"
                  placeholder="Digite a descrição"
                  className="w-full p-2 border border-gray-300 rounded"
                />
              </div>
              <div className="mb-4">
                <label className="block text-gray-700 font-bold mb-2">Horas</label>
                <input
                  type="number"
                  placeholder="Digite a quantidade de horas"
                  className="w-full p-2 border border-gray-300 rounded"
                />
              </div>
              <button
                type="submit"
                className="bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600"
              >
                Registrar
              </button>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

export default Dashboard;
