import { useContext, createContext, useEffect, useState } from 'react';
import {
  GoogleAuthProvider,
  signInWithPopup,
  signOut,
  onAuthStateChanged,
} from 'firebase/auth';
import { auth } from '../firebase';
import { useNavigate } from 'react-router-dom';
import Swal from 'sweetalert2';  // Importando SweetAlert para exibir a mensagem de erro

const AuthContext = createContext();

export const AuthContextProvider = ({ children }) => {
  const [user, setUser] = useState(null);
  const navigate = useNavigate();  // Usar o hook useNavigate para redirecionar

  const googleSignIn = async () => {
    const provider = new GoogleAuthProvider();
    try {
      const result = await signInWithPopup(auth, provider);
      
      const userEmail = result.user.email;

      // Verificar se o e-mail tem o domínio correto
      if (userEmail.endsWith('@aluno.ifsc.edu.br')) {
        // Se for aluno, redireciona para a página de aluno
        navigate('/account');
      } else if (userEmail.endsWith('@ifsc.edu.br')) {
        // Se for professor, redireciona para a página de professor
        navigate('/dashboard');
      } else {
        // Exibir mensagem de erro se o domínio do e-mail não for reconhecido
        Swal.fire({
          title: 'Erro!',
          text: 'E-mail não reconhecido. Tente usar um e-mail válido (@aluno.ifsc.edu.br ou @ifsc.edu.br).',
          icon: 'error',
          confirmButtonText: 'OK',
        });
      }
    } catch (error) {
      console.log('Erro no login:', error);
    }
  };

  const logOut = () => {
    signOut(auth);
  };

  useEffect(() => {
    const unsubscribe = onAuthStateChanged(auth, (currentUser) => {
      setUser(currentUser);
      console.log('User', currentUser);
    });
    return () => {
      unsubscribe();
    };
  }, []);

  return (
    <AuthContext.Provider value={{ googleSignIn, logOut, user }}>
      {children}
    </AuthContext.Provider>
  );
};

export const UserAuth = () => {
  return useContext(AuthContext);
};
