import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

const Dashboard = () => {
  const [selectedMenu, setSelectedMenu] = useState('overview'); // Controle do menu selecionado
  const navigate = useNavigate(); // Para redirecionar o usuário

  const menuItems = [
    { id: 'overview', label: 'Visão Geral' },
    { id: 'register-hours', label: 'Registrar Horas' },
    { id: 'reports', label: 'Relatórios' },
    { id: 'settings', label: 'Configurações' },
  ];

  const handleMenuClick = (id) => {
    setSelectedMenu(id);
    // Lógica para navegar entre diferentes seções
    if (id === 'register-hours') {
      navigate('/register-hours');
    }
  };

  return (
    <div className="flex h-screen">
      {/* Sidebar */}
      <aside className="w-64 bg-gray-800 text-white">
        <div className="p-6">
          <h1 className="text-2xl font-bold">Dashboard</h1>
        </div>
        <nav className="mt-6">
          <ul>
            {menuItems.map((item) => (
              <li
                key={item.id}
                onClick={() => handleMenuClick(item.id)}
                className={`px-6 py-3 cursor-pointer hover:bg-gray-700 ${
                  selectedMenu === item.id ? 'bg-gray-700' : ''
                }`}
              >
                {item.label}
              </li>
            ))}
          </ul>
        </nav>
      </aside>

      {/* Main Content */}
      <main className="flex-1 bg-gray-100 p-6">
        <header className="flex justify-between items-center pb-4 border-b">
          <h2 className="text-2xl font-semibold">
            {selectedMenu === 'overview'
              ? 'Visão Geral'
              : selectedMenu === 'register-hours'
              ? 'Registrar Horas'
              : selectedMenu === 'reports'
              ? 'Relatórios'
              : 'Configurações'}
          </h2>
          <button
            onClick={() => navigate('/logout')}
            className="bg-red-500 text-white px-4 py-2 rounded hover:bg-red-600"
          >
            Sair
          </button>
        </header>

        <div className="mt-6">
          {selectedMenu === 'overview' && (
            <p>Bem-vindo ao painel! Aqui você pode ver um resumo das suas atividades.</p>
          )}
          {selectedMenu === 'register-hours' && (
            <p>Aqui você pode registrar suas horas complementares.</p>
          )}
          {selectedMenu === 'reports' && (
            <p>Acesse relatórios detalhados de suas horas complementares.</p>
          )}
          {selectedMenu === 'settings' && (
            <p>Altere as configurações da sua conta ou do sistema.</p>
          )}
        </div>
      </main>
    </div>
  );
};

export default Dashboard;
