import React, { useEffect } from 'react';
import { Button, Box, Typography } from '@mui/material';
import { UserAuth } from '../context/AuthContext';
import { useNavigate } from 'react-router-dom';
import { getRedirectResult } from 'firebase/auth';
import { auth } from '../firebase'; // Certifique-se de importar corretamente

const Login = () => {
  const { googleSignIn, user } = UserAuth();
  const navigate = useNavigate();

  const handleGoogleSignIn = async () => {
    try {
      googleSignIn();
    } catch (error) {
      console.log("Erro ao fazer login com o Google:", error);
    }
  };

  useEffect(() => {
    // Verifica se o usuário foi redirecionado após o login
    const checkRedirectResult = async () => {
      try {
        const result = await getRedirectResult(auth);
        if (result && result.user) {
          navigate('/account'); // Redireciona para a conta se o login for bem-sucedido
        }
      } catch (error) {
        console.log("Erro ao processar redirecionamento:", error);
      }
    };

    checkRedirectResult();
  }, [navigate, user]);

  useEffect(() => {
    if (user) {
      navigate('/account'); // Redireciona se o usuário já estiver autenticado
    }
  }, [user, navigate]);

  return (
    <Box sx={{ textAlign: 'center', mt: 8 }}>
      <Typography variant="h3" component="h1" sx={{ mb: 4 }}>
        Login
      </Typography>
      <Box sx={{ maxWidth: 240, margin: '0 auto', py: 2 }}>
        <Button 
          variant="contained" 
          fullWidth
          onClick={handleGoogleSignIn}
        >
          Sign in with Google
        </Button>
      </Box>
    </Box>
  );
};

export default Login;
