import React, { useState } from 'react';
import { Button, TextField, Box, Typography } from '@mui/material';
import { GoogleAuthProvider, signInWithPopup } from 'firebase/auth';
import { auth } from '../firebase'; // Certifique-se de que o caminho do arquivo está correto
import { useNavigate } from 'react-router-dom';

const Login = () => {
  const [email, setEmail] = useState('');
  const [error, setError] = useState('');
  const navigate = useNavigate();

  const handleLogin = () => {
    if (!email) {
      setError('Por favor, insira um e-mail.');
      return;
    }

    // Verifica o domínio do e-mail e redireciona
    if (email.endsWith('@aluno')) {
      navigate('/aluno');
    } else if (email.endsWith('@ifsc')) {
      navigate('/professor');
    } else {
      setError('E-mail inválido! Não corresponde aos domínios esperados.');
    }
  };

  const handleGoogleLogin = async () => {
    const provider = new GoogleAuthProvider();

    try {
      await signInWithPopup(auth, provider);
      const user = auth.currentUser;
      if (user) {
        // Verifica o domínio do e-mail do usuário logado via Google
        if (user.email.endsWith('@aluno')) {
          navigate('/aluno');
        } else if (user.email.endsWith('@ifsc')) {
          navigate('/professor');
        } else {
          setError('E-mail inválido! Não corresponde aos domínios esperados.');
        }
      }
    } catch (err) {
      setError('Erro ao fazer login com o Google.');
      console.error(err);
    }
  };

  return (
    <Box display="flex" flexDirection="column" alignItems="center" mt={5}>
      <Typography variant="h4" gutterBottom>
        Login
      </Typography>
      <TextField
        label="E-mail"
        variant="outlined"
        fullWidth
        value={email}
        onChange={(e) => setEmail(e.target.value)}
        margin="normal"
      />
      <Button
        variant="contained"
        color="primary"
        onClick={handleLogin}
        fullWidth
        sx={{ mt: 2 }}
      >
        Entrar com E-mail
      </Button>

      <Button
        variant="contained"
        color="secondary"
        onClick={handleGoogleLogin}
        fullWidth
        sx={{ mt: 2 }}
      >
        Entrar com Google
      </Button>

      {error && <Typography color="error" sx={{ mt: 2 }}>{error}</Typography>}
    </Box>
  );
};

export default Login;
