// AuthContext.js (apenas a parte relevante)
import { createContext, useContext, useEffect, useState } from 'react';
import { auth } from '../firebase'; // Ajuste para o seu projeto
import { onAuthStateChanged } from 'firebase/auth';

const AuthContext = createContext();

export const AuthContextProvider = ({ children }) => {
  const [user, setUser] = useState(null);
  const [userType, setUserType] = useState(''); // Pode ser 'aluno' ou 'professor'

  useEffect(() => {
    const unsubscribe = onAuthStateChanged(auth, (currentUser) => {
      if (currentUser) {
        // Aqui, simule a lógica para definir se o usuário é 'aluno' ou 'professor'
        const type = currentUser.email.includes('professor') ? 'professor' : 'aluno';
        setUserType(type);
      }
      setUser(currentUser);
    });
    return () => unsubscribe();
  }, []);

  return (
    <AuthContext.Provider value={{ user, userType }}>
      {children}
    </AuthContext.Provider>
  );
};

export const UserAuth = () => useContext(AuthContext);
