import React from 'react';
import { Link } from 'react-router-dom';
import { UserAuth } from '../context/AuthContext';

const Navbar = () => {
  const { user, logOut } = UserAuth();

  const handleSignOut = async () => {
    try {
      await logOut();
    } catch (error) {
      console.error("Error during sign out:", error);
    }
  };

  return (
    <nav className="flex items-center justify-between bg-blue-600 text-white p-4 shadow-md">
      {/* Logo ou Título */}
      <h1 className="text-2xl font-bold">
        <Link to="/" className="hover:text-gray-200">
          Horas Complementares
        </Link>
      </h1>

      {/* Navegação ou Botões de Autenticação */}
      <div className="flex items-center space-x-4">
        {user?.displayName ? (
          <div className="flex items-center space-x-4">
            <span className="hidden sm:inline text-lg font-medium">
              Olá, {user.displayName}
            </span>
            <button
              onClick={handleSignOut}
              className="bg-red-500 hover:bg-red-600 text-white py-2 px-4 rounded transition-all"
              aria-label="Sign out"
            >
              Logout
            </button>
          </div>
        ) : (
          <Link
            to="/signin"
            className="bg-green-500 hover:bg-green-600 text-white py-2 px-4 rounded transition-all"
            aria-label="Sign in"
          >
            Sign In
          </Link>
        )}
      </div>
    </nav>
  );
};

export default Navbar;
