import React, { useState } from "react";
import { GoogleOAuthProvider, GoogleLogin } from "@react-oauth/google";

function App() {
    const [name, setName] = useState('');
    const [email, setEmail] = useState('');
    const [profilePic, setProfilePic] = useState('');
    const [isLoggedIn, setIsLoggedIn] = useState(false);

    const responseGoogle = (credentialResponse) => {
        const { credential } = credentialResponse; // Token JWT retornado
        console.log("Token de credencial:", credential);

        // Decodificar o token JWT (opcionalmente) para extrair dados do usuário
        const userInfo = JSON.parse(atob(credential.split('.')[1]));
        console.log("Informações do usuário:", userInfo);

        // Definir as informações do usuário
        setName(userInfo.name);
        setEmail(userInfo.email);
        setProfilePic(userInfo.picture); // Se o campo 'picture' estiver disponível
        setIsLoggedIn(true);
    };

    return (
        <GoogleOAuthProvider clientId="330895842307-cprcpq4q042vvl6mcjqlvgpem1ip8nf4.apps.googleusercontent.com">
            <div className="container">
                <GoogleLogin
                    onSuccess={responseGoogle}
                    onError={() => {
                        console.log("Falha ao fazer login.");
                    }}
                />
                {isLoggedIn ? (
                    <div style={{ textAlign: "center" }}>
                        <h1>Informações do Usuário</h1>
                        <img className="profile" src={profilePic} alt="Profile" />
                        <p>Nome: {name}</p>
                        <p>Email: {email}</p>
                    </div>
                ) : (
                    <p style={{ textAlign: "center" }}>Por favor, faça login para ver suas informações.</p>
                )}
            </div>
        </GoogleOAuthProvider>
    );
}

export default App;
