import React, { useEffect } from 'react';
import Swal from 'sweetalert2';
import { UserAuth } from '../context/AuthContext';
import { useNavigate } from 'react-router-dom';

const Account = () => {
  const { logOut, user } = UserAuth();
  const navigate = useNavigate(); // Hook para redirecionar para outras páginas

  const handleSignOut = async () => {
    try {
      await logOut();
    } catch (error) {
      console.log(error);
    }
  };

  useEffect(() => {
    Swal.fire({
      title: 'Bem-vindo!',
      text: 'Aqui você pode cadastrar e acompanhar suas horas complementares.',
      icon: 'success',
      confirmButtonText: 'OK'
    });
  }, []);

  const handleRegisterClick = () => {
    navigate('/register'); // Altere '/register' para a rota correta da sua página de cadastro
  };

  return (
    <div className="p-6 bg-gray-100 min-h-screen">
      <h1 className="text-2xl font-bold mb-4">Bem-vindo, {user?.displayName || 'Usuário'}</h1>
      <button
        onClick={handleSignOut}
        className="bg-red-500 text-white px-4 py-2 rounded mb-4 hover:bg-red-600"
      >
        Sair
      </button>
      <button
        onClick={handleRegisterClick}
        className="bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600"
      >
        Registrar horas
      </button>
    </div>
  );
};

export default Account;
