import React, { createContext, useContext, useState } from 'react';
import { GoogleAuthProvider, signInWithPopup, signOut } from 'firebase/auth';
import { auth } from '../firebase'; // Caminho correto do arquivo firebaseConfig.js

// Criação do contexto de autenticação
const AuthContext = createContext();

// Provedor de autenticação que envolve a aplicação
export const AuthContextProvider = ({ children }) => {
  const [user, setUser] = useState(null);

  // Função para login com Google
  const googleSignIn = async () => {
    const provider = new GoogleAuthProvider();
  
    try {
      const result = await signInWithPopup(auth, provider);
      const loggedInUser = result.user;
      setUser(loggedInUser); // Armazena o usuário no estado
    } catch (err) {
      console.error("Erro ao fazer login com o Google", err);
    }
  };
  

  // Função para logout
  const logOut = async () => {
    try {
      await signOut(auth);
      setUser(null);  // Limpa o usuário no estado
    } catch (err) {
      console.error("Erro ao fazer logout", err);
    }
  };

  return (
    <AuthContext.Provider value={{ user, googleSignIn, logOut }}>
      {children}
    </AuthContext.Provider>
  );
};

// Hook para acessar o contexto de autenticação em qualquer lugar da aplicação
export const UserAuth = () => useContext(AuthContext);
