import { useContext, createContext, useEffect, useState } from 'react';
import {
  GoogleAuthProvider,
  signInWithPopup,
  signOut,
  onAuthStateChanged,
} from 'firebase/auth';
import { auth } from '../firebase';

const AuthContext = createContext();

export const AuthContextProvider = ({ children }) => {
  const [user, setUser] = useState(null);

  const googleSignIn = async () => {
    const navigate = useNavigate(); // Para redirecionar o usuário
  
    try {
      const provider = new GoogleAuthProvider();
      const result = await signInWithPopup(auth, provider);
  
      // Obter o e-mail do usuário logado
      const email = result.user.email;
  
      if (email.endsWith('@aluno')) {
        navigate('/account'); // Redirecionar para a página de aluno
      } else if (email.endsWith('@ifsc')) {
        navigate('/dashboard'); // Redirecionar para a página de professor
      } else {
        console.log('Domínio de e-mail não reconhecido');
        // Opcional: redirecionar para uma página de erro ou exibir mensagem
      }
    } catch (error) {
      console.log('Erro no login:', error);
    }
  };

  const logOut = () => {
    signOut(auth);
  };

  useEffect(() => {
    const unsubscribe = onAuthStateChanged(auth, (currentUser) => {
      setUser(currentUser);
      console.log('User', currentUser);
    });
    return () => {
      unsubscribe();
    };
  }, []);

  return (
    <AuthContext.Provider value={{ googleSignIn, logOut, user }}>
      {children}
    </AuthContext.Provider>
  );
};

export const UserAuth = () => {
  return useContext(AuthContext);
};
