import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { UserAuth } from '../context/AuthContext'; // Ou o caminho correto do seu contexto

const Login = () => {
  const { logIn, user } = UserAuth(); // Supondo que você tenha a função de login no contexto
  const navigate = useNavigate();
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');

  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      await logIn(email, password); // Lógica de login

      // Após o login, redireciona conforme o tipo de usuário
      if (user && user.role === 'aluno') {
        navigate('/account'); // Redireciona para a página Account se for aluno
      } else {
        navigate('/outro'); // Redireciona para outra página se não for aluno
      }
    } catch (error) {
      console.log(error);
    }
  };

  return (
    <div>
      <form onSubmit={handleLogin}>
        <input
          type="email"
          placeholder="Email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
        />
        <input
          type="password"
          placeholder="Senha"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
        />
        <button type="submit">Login</button>
      </form>
    </div>
  );
};

export default Login;
