import { useContext, createContext, useEffect, useState } from 'react';
import {
  GoogleAuthProvider,
  signInWithPopup,
  signOut,
  onAuthStateChanged,
  setPersistence,
  browserSessionPersistence,
} from 'firebase/auth';
import { auth } from '../firebase';
import { useNavigate } from 'react-router-dom';  // Importar useNavigate para redirecionamento

const AuthContext = createContext();

export const AuthContextProvider = ({ children }) => {
  const [user, setUser] = useState(null);
  const navigate = useNavigate();  // Usar o hook useNavigate para redirecionar

  const googleSignIn = async () => {
    const provider = new GoogleAuthProvider();
    try {
      // Configura persistência para a sessão
      await setPersistence(auth, browserSessionPersistence); 
      
      // Faz o login com o Google
      const result = await signInWithPopup(auth, provider);
      
      // Obtém o e-mail do usuário logado
      const email = result.user.email;

      // Verifica se o e-mail é de aluno ou professor
      if (email && email.endsWith('@aluno.ifsc.edu.br')) {
        // Redireciona para a página de aluno
        navigate('/account');
      } else if (email && email.endsWith('@ifsc.edu.br')) {
        // Redireciona para a página de professor
        navigate('/dashboard');
      } else {
        // Caso o domínio do e-mail não seja reconhecido
        console.log('Domínio de e-mail não reconhecido');
        // Opcional: você pode redirecionar para uma página de erro ou exibir uma mensagem
      }
    } catch (error) {
      console.log('Erro no login:', error);
    }
  };

  const logOut = () => {
    signOut(auth);
  };

  useEffect(() => {
    const unsubscribe = onAuthStateChanged(auth, (currentUser) => {
      setUser(currentUser);
      console.log('User', currentUser);
    });
    return () => {
      unsubscribe();
    };
  }, []);

  return (
    <AuthContext.Provider value={{ googleSignIn, logOut, user }}>
      {children}
    </AuthContext.Provider>
  );
};

export const UserAuth = () => {
  return useContext(AuthContext);
};
