import { useContext, createContext, useEffect, useState } from 'react';
import {
  GoogleAuthProvider,
  signInWithPopup,
  signOut,
  onAuthStateChanged,
} from 'firebase/auth';
import { auth } from '../firebase'; // Certifique-se de que o Firebase está configurado corretamente

// Cria o contexto de autenticação
const AuthContext = createContext();

// Provedor de contexto para gerenciar o estado de autenticação
export const AuthContextProvider = ({ children }) => {
  const [user, setUser] = useState(null); // Estado para armazenar os dados do usuário
  const [loading, setLoading] = useState(true); // Estado para rastrear o carregamento

  // Função para login com Google
  const googleSignIn = async () => {
    try {
      const provider = new GoogleAuthProvider();
      await signInWithPopup(auth, provider); // Realiza o login com popup
    } catch (error) {
      console.error('Erro ao fazer login:', error.message);
    }
  };

  // Função para logout
  const logOut = async () => {
    try {
      await signOut(auth); // Realiza o logout
      setUser(null); // Limpa os dados do usuário localmente
    } catch (error) {
      console.error('Erro ao sair:', error.message);
    }
  };

  // Observa o estado de autenticação
  useEffect(() => {
    const unsubscribe = onAuthStateChanged(auth, (currentUser) => {
      setUser(currentUser); // Atualiza o estado do usuário
      setLoading(false); // Marca o carregamento como concluído
      console.log('Usuário autenticado:', currentUser);
    });

    // Limpeza do observador quando o componente é desmontado
    return () => unsubscribe();
  }, []);

  // Provedor do contexto
  return (
    <AuthContext.Provider value={{ user, googleSignIn, logOut, loading }}>
      {children}
    </AuthContext.Provider>
  );
};

// Hook personalizado para acessar o contexto
export const UserAuth = () => useContext(AuthContext);
